      </Layout>
    </Router>
  )
}

const PrivateRoute = ({component: Component, ...rest}) => {
  const {loading, error, data} = useQuery(IS_LOGGED_IN)
  // –ï—Å–ª–∏ –¥–∞–Ω–Ω—ã–µ –∑–∞–≥—Ä—É–∂–∞—é—Ç—Å—è, –≤—ã–≤–æ–¥–∏–º —Å–æ–æ–±—â–µ–Ω–∏–µ –æ –∑–∞–≥—Ä—É–∑–∫–µ
  if (loading) return <p>Loading...</p>;
// –ï—Å–ª–∏ –ø—Ä–∏ –ø–æ–ª—É—á–µ–Ω–∏–∏ –¥–∞–Ω–Ω—ã—Ö –ø—Ä–æ–∏–∑–æ—à–µ–ª —Å–±–æ–π, –≤—ã–≤–æ–¥–∏–º —Å–æ–æ–±—â–µ–Ω–∏–µ –æ–± –æ—à–∏–±–∫–µ
  if (error) return <p>Error!</p>;
// –ï—Å–ª–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –∞–≤—Ç–æ—Ä–∏–∑–æ–≤–∞–Ω, –Ω–∞–ø—Ä–∞–≤–ª—è–µ–º –µ–≥–æ –∫ –∑–∞–ø—Ä–∞—à–∏–≤–∞–µ–º–æ–º—É –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—É
// –í –ø—Ä–æ—Ç–∏–≤–Ω–æ–º —Å–ª—É—á–∞–µ –ø–µ—Ä–µ–Ω–∞–ø—Ä–∞–≤–ª—è–µ–º –Ω–∞ —Å—Ç—Ä–∞–Ω–∏—Ü—É –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏–∏
  return (
    <Route {...rest}
      render={props =>
        data.isLoggedIn === true ? (
          <Component {...props}/>
        ): (
          <Redirect to={{
            pathname: '/signin',
