  isLoggedIn: !!localStorage.getItem('token')
}
// –ó–∞–ø–∏—Å—ã–≤–∞–µ–º –¥–∞–Ω–Ω—ã–µ –∫—ç—à–∞ –ø—Ä–∏ –Ω–∞—á–∞–ª—å–Ω–æ–π –∑–∞–≥—Ä—É–∑–∫–µ
cache.writeData({data})
// –ó–∞–ø–∏—Å—ã–≤–∞–µ–º –¥–∞–Ω–Ω—ã–µ –∫—ç—à–∞ –ø–æ—Å–ª–µ –µ–≥–æ —Å–±—Ä–æ—Å–∞
client.onResetStore(() => cache.writeData({data}))

const App = () => {
  return(
    <ApolloProvider client={client}>
      <GlobalStyle/>
      <Pages/>
    </ApolloProvider>
  )
}
