import React from 'react'
import ReactDOM from 'react-dom'
import {
  ApolloClient,
  ApolloProvider,
  HttpLink,
  InMemoryCache,
  fromPromise,
  ApolloLink
} from "@apollo/client";
// Apps
import {App} from './app/App'
import {MetronicI18nProvider} from './_metronic/i18n/Metronici18n'

/**
 * TIP: Replace this style import with dark styles to enable dark mode
 *
 * import './_metronic/assets/sass/style.dark.scss'
 **/
import './_metronic/assets/sass/style.scss'
import {setContext} from "@apollo/client/link/context";
import {onError} from "@apollo/client/link/error";
import {REFRESH_TOKEN} from "./apollo/apollo/queries/authAdmin";

/**
 * Base URL of the website.
 *
 * @see https://facebook.github.io/create-react-app/docs/using-the-public-folder
 */
const {PUBLIC_URL} = process.env


// Init Apollo================

let client: ApolloClient<any>
const httpLink = new HttpLink({uri: 'https://retter-local.dev-test.pro/graphql'})

const withTokenLink = setContext((_, {headers}) => {
  const token = localStorage.getItem('admin_access_token');
  return {
    headers: {
      ...headers,
      authorization: token ? `Bearer ${token}` : "",
    }
  }
});

interface IRefreshResponse {
  refreshToken: {
    refreshToken: string
    accessToken: string
  }

  [key: string]: any
}

const getNewTokenPair = () => {
  const refreshToken = localStorage.getItem('admin_refresh_token')
  return client.mutate({mutation: REFRESH_TOKEN, variables: {refreshToken}})
    .then((data) => {
      console.log('DATA', data)
      if (data.data) {
        return data.data?.refreshToken;
      }
    })
    .catch(err => {
      console.log('$$$>>', err)
      // return
    });
};

const tokenErrorLink = onError(
  ({graphQLErrors, networkError, operation, forward}) => {
    if (graphQLErrors) {
      for (let err of graphQLErrors) {
        if (err.message === 'Access token error: jwt expired') {
          return fromPromise(
            getNewTokenPair().catch((error) => {
              console.log('from getNewTokenPair>> ', error)
              return;
            })
          )
            .filter((data = {}) => Boolean(data?.accessToken) && Boolean(data?.refreshToken))
            .flatMap(({accessToken, refreshToken}) => {
              const oldHeaders = operation.getContext().headers;
              operation.setContext({
                headers: {
                  ...oldHeaders,
                  authorization: `Bearer ${accessToken}`,
                },
              });

              localStorage.setItem('admin_access_token', accessToken)
              localStorage.setItem('admin_refresh_token', refreshToken)

              return forward(operation);
            });
        }
      }
    }
  }
);

client = new ApolloClient({
  link: ApolloLink.from([tokenErrorLink, withTokenLink, httpLink]),
  cache: new InMemoryCache()
});


ReactDOM.render(
  <ApolloProvider client={client}>
    <MetronicI18nProvider>
      <App basename={PUBLIC_URL}/>
    </MetronicI18nProvider>
  </ApolloProvider>,
  document.getElementById('root')
)
```

