``` js
const EditUserContent = () => {
  const [success, setSuccess] = useState(false)
  const [fail, setFail] = useState(false)
  const [msg, setMessage] = useState('')
  const client = useApolloClient()
  const id = useGetIdFromUrl()

  const [getUser, {data}] = useLazyQuery(GET_USER, {
    variables: {
      id:  id
    }
  })

  const [updateUser, { loading, error}] = useUpdateUser()

  const formik = useFormik({
    initialValues,
    validationSchema: editUserSchema,
    onSubmit: (values) => {

      const date = client.readQuery({
        query: READ_DATE,
      });

      updateUser({variables: {
        ...values,
          id: id,
          is_admin: !!+values.is_admin,
          active: +values.active,
          plan_id: +values.plan_id,
          plan_trial_done: +values.plan_trial_done,
          plan_expiration_date: date.date.date
      }})
        .then(() => {
          window.scrollTo(0, 0)
          getUser()
          setSuccess(true)
        })
        .catch(err => setFail(true))
    },
  })

  useEffect(() => {getUser()}, [])

  useEffect(() => {
    if(data){
      if(!!localStorage.getItem('new-user')){
        localStorage.removeItem('new-user')
        setSuccess(true)
        setMessage('User created successfully')
      }

      formik.setValues({
        password: '',
        plan_trial_done: data.getUserById.plan_trial_done,
        plan_id: data.getUserById.plan.id,
        is_admin: data.getUserById.is_admin ? '1' : '0',
        active: data.getUserById.active,
        email: data.getUserById.email,
        name: data.getUserById.name,
        changepassword: '',
        plan_expiration_date: data.getUserById.plan_expiration_date
      });
    }
  }, [data])

  const resetNotifications = () => {
    setSuccess(false)
    setFail(false)
  }


  return (
    <div>
     <EditUserNotifications setSuccess={setSuccess}  setFail={setFail}  success={success} fail={fail} error={error && error.message} msg={msg} setMessage={setMessage}/>
      <div className="w-100 p-5 rounded-1 dev-view-user-main">
        <EditUserForm formik={formik} loading={loading} resetNotifications={resetNotifications}/>
      </div>
    </div>
  );
};

export default EditUserContent;
```

